const boutonsFiltres=Array.from(document.querySelectorAll(".filters-menu button")),reponse=await fetch("photo.json"),photos=await reponse.json();const grid=document.getElementById("static-thumbnails");function afficherTout(){grid.innerHTML="";for(let t=0;t<photos.length;t++){let e=document.createElement("a"),r=document.createElement("img");e.href=photos[t].image,e.setAttribute("data-fslightbox","gallery"),r.src=photos[t].appercu,r.setAttribute("alt",photos[t].description),e.appendChild(r),grid.appendChild(e)}refreshFsLightbox()}function afficherPics(t){grid.innerHTML="";for(let e=0;e<t.length;e++){let r=document.createElement("a"),o=document.createElement("img");r.href=t[e].image,r.setAttribute("data-fslightbox","gallery"),o.src=t[e].appercu,o.setAttribute("alt",t[e].description),r.appendChild(o),grid.appendChild(r)}refreshFsLightbox()}afficherTout(),boutonsFiltres.forEach(t=>{t.addEventListener("click",e=>{e.preventDefault();let r=t.id;if("tous"===r)afficherTout();else{let o=photos.filter(function(t){return t.categorie===r});afficherPics(o)}})});for(let i=0;i<boutonsFiltres.length;i++)boutonsFiltres[i].addEventListener("click",t=>{t.preventDefault(),boutonsFiltres.forEach(e=>{let r=t.target;e.classList.remove("target"),r.classList.add("target")})});